#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#include <stdio.h>
#include <stdint.h>

void delay(void)
{
  for(uint32_t i=0; i<300000; i++);
}

int main(void)
{
  //Peripheral Register Addresses
  uint32_t volatile *const pGPIOModeReg = (uint32_t*)(0x40020C00);
  uint32_t volatile *const pInputDataReg = (uint32_t*)(0x40020C00+0x10);
  uint32_t volatile *const pOutputDataReg = (uint32_t*)(0x40020C00+0x14);
  uint32_t volatile *const pClockCtrlReg = (uint32_t*)(0x40020C00+0x30);
  uint32_t volatile *const pPullUpDownReg = (uint32_t*)(0x40020C00+0x0C);

  //1. Enable the peripheral clock of GPIOD Peripheral
  *pClockCtrlReg |= (1 << 3);

  //2. Configure PD0,PD1,PD2,PD3 as output (rows)
  *pGPIOModeReg &= ~(0x55); //Clear
  *pGPIOModeReg |= 0x55; //Set

  //3. Configure PD8,PD9,PD10,PD11 as input (columns)
  *pGPIOModeReg &= ~(0xFF << 16);

  //4. Configure the pull up registers for PD8, PD9, PD10 and PD11
  *pPullUpDownReg &= ~(0xFF << 16); //Clear
  *pPullUpDownReg |= (0x55 << 16); //Set

	while(1)
	{
	  // Make all rows HIGH
	  *pOutputDataReg |= 0x0f;

	  // Make R1 Low (PD0)
	  *pOutputDataReg &= ~(1 << 0);

	  // Scan the Columns
	  // Check C1 (PD8) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 8)))
	  {
		// Key is pressed
		delay();
		printf("1\n");
	  }

	  // Check C2 (PD9) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 9)))
	  {
		// Key is pressed
		delay();
		printf("2\n");
	  }

	  // Check C3 (PD10) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 10)))
	  {
		// Key is pressed
		delay();
		printf("3\n");
	  }

	  // Check C4 (PD11) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 11)))
	  {
		// Key is pressed
		delay();
		printf("A\n");
	  }

	  // Make all rows HIGH
	  *pOutputDataReg |= 0x0f;

	  // Make R2 Low (PD1)
	  *pOutputDataReg &= ~(1 << 1);

	  // Scan the Columns
	  // Check C1 (PD8) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 8)))
	  {
		// Key is pressed
		delay();
		printf("4\n");
	  }

	  // Check C2 (PD9) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 9)))
	  {
		// Key is pressed
		delay();
		printf("5\n");
	  }

	  // Check C3 (PD10) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 10)))
	  {
		 // Key is pressed
		 delay();
		 printf("6\n");
	  }

	  // Check C4 (PD11) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 11)))
	  {
		// Key is pressed
		delay();
		printf("B\n");
	  }

	  // Make all rows HIGH
	  *pOutputDataReg |= 0x0f;

	  // Make R3 Low (PD2)
	  *pOutputDataReg &= ~(1 << 2);

	  // Scan the Columns
	  // Check C1 (PD8) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 8)))
	  {
		// Key is pressed
		delay();
		printf("7\n");
	  }

	  // Check C2 (PD9) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 9)))
	  {
		// Key is pressed
		delay();
		printf("8\n");
	  }

	  // Check C3 (PD10) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 10)))
	  {
		 // Key is pressed
		 delay();
		 printf("9\n");
	  }

	  // Check C4 (PD11) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 11)))
	  {
		// Key is pressed
		delay();
		printf("C\n");
	  }

	  // Make all rows HIGH
	  *pOutputDataReg |= 0x0f;

	  // Make R4 Low (PD3)
	  *pOutputDataReg &= ~(1 << 3);

	  // Scan the Columns
	  // Check C1 (PD8) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 8)))
	  {
		// Key is pressed
		delay();
		printf("*\n");
	  }

	  // Check C2 (PD9) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 9)))
	  {
		// Key is pressed
		delay();
		printf("0\n");
	  }

	  // Check C3 (PD10) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 10)))
	  {
		 // Key is pressed
		 delay();
		 printf("#\n");
	  }

	  // Check C4 (PD11) LOW or HIGH
	  if (!(*pInputDataReg & (1 << 11)))
	  {
		// Key is pressed
		delay();
		printf("D\n");
	  }
	}
}
